import Questions from '../data/TCSNQT1.js'
import React, { useState, useRef } from 'react'

import {
    BrowserRouter as Router, Routes, Route,
    Navigate, Link
} from 'react-router-dom';


function Live() {

    const Questionslist = Questions[0].QA;
    const [count, setCount] = useState(1);
    var score=0;
