import React from 'react'
import "../../CSS/Userdashboard/Statistics.css"
import Chart from 'chart.js/auto';

import { Doughnut } from 'react-chartjs-2';
import { Pie } from "react-chartjs-2";



function Statistics() {


    chartData = [
        {
          id: 1,
          year: 2016,
          userGain: 80000,
          userLost: 823
        },
        {
          id: 2,
          year: 2017,
          userGain: 45677,
          userLost: 345
        },
        {
          id: 3,
          year: 2018,
          userGain: 78888,
          userLost: 555
        },
        {
          id: 4,
          year: 2019,
          userGain: 90000,
          userLost: 4555
        },
        {
          id: 5,
          year: 2020,
          userGain: 4300,
          userLost: 234
        }
      ];


    
  return (
    <div className="statistics-page" >
<h2 id="statistics-page-title">
    Statistics
</h2>



<div className="chart-container">
      <h2 style={{ textAlign: "center" }}>Pie Chart</h2>
      <Pie
        data={chartData}
        options={{
          plugins: {
            title: {
              display: true,
              text: "Users Gained between 2016-2020"
            }
          }
        }}
      />
    </div>








    </div>
  )
}

export default Statistics
