import { Pie } from "react-chartjs-2";


function Statistics() {


    

    var scorestats = [];
    var noqstats=[];
    var i=0;
    var examstats=sessionStorage.getItem("examstats")
    // {(examstats).map((exam)=>{
    //     scorestats[i]=  exam.score
    //     noqstats[i]=exam.totalquestions
    //     i++;
    // })
    // }
    console.log(JSOnexamstats)


    const labels = noqstats;
    const data = {
      labels: labels,
      datasets: [
        {
          label: "Score vs Marks",
          backgroundColor: "rgb(255, 99, 132)",
          borderColor: "rgb(255, 99, 132)",
          data: scorestats,
        },
      ],
    }


    

  return (
    <div className="statistics-page" >
<h1 id="statistics-page-title">
    STATISTICS
</h1>

{/* {JSON.parse(examstats).map((exam)=>{
    return (<p>{exam.examname}</p>)
})} */}


<div className='statistics-bargraph'>
      <Bar data={data} />
    </div>

    <div className='statistics-piegraph'>
    <Pie data={data} />
    </div>

    







    </div>
  )
}

export default Statistics
