import React from 'react'
import "../../CSS/Userdashboard/Statistics.css"
import Chart from 'chart.js/auto';
import { Bar } from "react-chartjs-2";

import { Pie } from "react-chartjs-2";


function Statistics() {


    const labels = ["January", "February", "March", "April", "May", "June"];
    const data = {
      labels: labels,
      datasets: [
        {
          label: "My First dataset",
          backgroundColor: "rgb(255, 99, 132)",
          borderColor: "rgb(255, 99, 132)",
          data: [0, 10, 5, 2, 20, 30, 45],
        },
      ],
    }

    var examstats=sessionStorage.getItem("examstats")
    console.log(examstats)
    
  return (
    <div className="statistics-page" >
<h1 id="statistics-page-title">
    STATISTICS
</h1>

{[examstats].map((exam)=>{
    return (<p>{exam.examname}</p>)
})}


<div className='statistics-bargraph'>
      <Bar data={data} />
    </div>

    <div className='statistics-piegraph'>
    <Pie data={data} />
    </div>

    







    </div>
  )
}

export default Statistics
