import '../CSS/Signup.css';
import {useState} from 'react'
import React from 'react'

function Signup () 
{

const [user,setUser] =useState(
    {
        name: "",
        email: "",
        phone: "",
        password: "",
        cpassword: ""

    }
);

let name,value;
const handleInputs=(e)=>
{
    name=e.target.name;
    value=e.target.value;
    setUser({...user,[name]:value});
}


const PostData=async(e)=>
{

    e.preventDefault();
    const  {name,email,phone,password,cpassword}=user;

    const res= await fetch("/register",
    {
        method: "POST",
        headers: {
            "Content-Type":"application/json"
        },
        
    })

}


    return( 
<>
<div className ="Signup">

<h1>
    Hi Welcome!
</h1>
<h5>
    Let's create an account
</h5>




<form method="POST" >

<input type="email" name="" placeholder="Email" id="" value={user.email} onChange={handleInputs}/>
<input type="text" name="" placeholder="Full name" id="" value={user.name} onChange={handleInputs}/>
<input type="number" name="" placeholder="phone" id="" value={user.phone} onChange={handleInputs}/>
<input type="password" name="" placeholder="password" id="" value={user.password} onChange={handleInputs}/>
<input type="password" name="" placeholder="confirm password" id="" value={user.cpassword} onChange={handleInputs}/>


<input type="submit" name="" value="Signup" id="submit" onClick={PostData}/>


</form>

<h5>
    Already have an account? <span> <a href="Login"> Login</a> </span>
</h5>

</div>




</>



    );
}

export default Signup;
